package EX;

public class Q04 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int x=1; x<=10; x++) {
			for(int y=1; y<=10; y++) {
				if((4*x)+(5*y)==60);
					System.out.println(x+","+y);
			}
		}

	}

}
//for 이용해서 4x+5y=60의 모든 해를 구해보기. 단, x와 y는 10 이하의 자연수